def stddate_to_jd (std_date):
    fmt='%Y-%m-%d'
    converted_date = datetime.datetime.strptime(std_date, fmt)
    converted_date = converted_date.timetuple()
    jul_date = str(converted_date.tm_year) + '' + str(converted_date.tm_yday)
    return(jul_date)

